import React from 'react';
import Columns from './Columns';
import Talleres from './Talleres';
import Matrix from './Matrix';

const Main = ({ darkMode, selectedNavItem }) => {

  const changeSection = (section, text) => {
    setSelectedNavItem(section);
    setSectionText(text);
  };
  const renderContent = () => {
    console.log(Talleres)
    switch (selectedNavItem) {
      case 'Home':
        return <Matrix darkMode={darkMode} changeSection={selectedNavItem} />;
      case 'Talleres':
        return <Columns />;
      case 'Marcas':
        return <Columns />;
      case 'Servicios':
        return <Columns />;
      case 'OtraSeccion':
        return <Talleres />;        
      default:
        return <Matrix />;
    }
  };
  const mainClasses = darkMode ? 'bg-dark text-light container-fluid' : 'bg-light text-dark container-fluid';

  return (
    <main className={`main-section ${mainClasses}`}>
        <div className="row vh-100">
            <div className="col-12">
              {renderContent()}
            </div>
        </div>
    </main>

  );
};

export default Main;
