import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Matrix = ({ darkMode }) => {
  const [matrixData, setMatrixData] = useState([
    ['', '', ''],
    ['', '', ''],
    ['', '', ''],
  ]);
  const [draggedItem, setDraggedItem] = useState(null);

  const matrixClasses = darkMode ? 'bg-light' : 'bg-dark text-light';
  const itemClasses = darkMode ? 'border-light' : 'border-dark';

  const handleDragStart = (rowIndex, columnIndex) => {
    setDraggedItem({ row: rowIndex, column: columnIndex });
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
  };

  const handleDrop = (targetRowIndex, targetColumnIndex) => {
    const updatedMatrix = [...matrixData];
    const draggedItemValue = updatedMatrix[draggedItem.row][draggedItem.column];

    updatedMatrix[draggedItem.row][draggedItem.column] = '';
    updatedMatrix[targetRowIndex][targetColumnIndex] = draggedItemValue;

    setMatrixData(updatedMatrix);
    setDraggedItem(null);
  };

  return (
    <div className={`matrix ${matrixClasses}`}>
      {matrixData.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((item, columnIndex) => (
            <div
              key={columnIndex}
              draggable={true}
              onDragStart={() => handleDragStart(rowIndex, columnIndex)}
              onDragEnd={handleDragEnd}
              onDrop={() => handleDrop(rowIndex, columnIndex)}
              onDragOver={(e) => e.preventDefault()}
            >
              {item}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Matrix;
