import React from 'react';
import Columns from './Columns';
import Talleres from './Talleres';

const Main = ({ darkMode, selectedNavItem }) => {
  // Función para renderizar el contenido según el elemento de navegación seleccionado
  const renderContent = () => {
    switch (selectedNavItem) {
      case 'talleres':
        return <Columns />;
      default:
        return <div>Contenido predeterminado del Main</div>;
    }
  };
  const mainClasses = darkMode ? 'bg-dark text-light container-fluid' : 'bg-light text-dark container-fluid';

  return (
    <main className={`main-section ${mainClasses}`}>
        <div className="row vh-100">
            <div className="col-12">
              {renderContent()}
            </div>
        </div>
    </main>

  );
};

export default Main;
