import React, {useState} from 'react';
import { Card, Form, Button } from 'react-bootstrap';
import { Tab, Tabs } from 'react-bootstrap';

const InformacionFacturacion = ({ darkMode, changeComponent, selectedImages }) => {
  const mainClasses = darkMode ? 'bg-dark text-light container-fluid' : 'bg-light text-dark container-fluid';
  const [activeTab, setActiveTab] = useState('tab1');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica de envío del formulario
  };

  return (
    <main className={`main-section ${mainClasses}`}>
      <div className="row vh-100 align-items-start pt-4">
        <div className="col-12 d-flex justify-content-center">
          <Form onSubmit={handleSubmit}>
            <Tabs activeKey={activeTab} onSelect={(key) => setActiveTab(key)}>
              <Tab eventKey="tab1" title="Informacion de Facturacion" style={{ border: '1px solid #ccc', borderRadius: '5px',width: '500px', height: '300px' }}>
                <Card style={{ width: '500px', height: '300px' }}>
                  <Card.Body>
                    <Form.Group controlId="frmTipoIdentificacion">
                      <Form.Label>Tipo de Identificación</Form.Label>
                      <Form.Control as="select" defaultValue="">
                        <option value="" disabled hidden>Seleccione un tipo</option>
                        <option value="cedula">Cédula</option>
                        <option value="pasaporte">Pasaporte</option>
                        <option value="licencia">Licencia de conducir</option>
                      </Form.Control>
                    </Form.Group>
                    <Form.Group controlId="formNombre">
                      <Form.Label>Numero de Identificacion</Form.Label>
                      <Form.Control type="text" placeholder="Ingrese su nombre" />
                    </Form.Group>
                    <Form.Group controlId="formNombre">
                      <Form.Label>Nombre</Form.Label>
                      <Form.Control type="text" placeholder="Ingrese su nombre" />
                    </Form.Group>
                  </Card.Body>
                </Card>
              </Tab>
              <Tab eventKey="tab2" title="Informacion de Entrega" style={{ border: '1px solid #ccc', borderRadius: '5px',width: '500px', height: '300px' }}>
              </Tab>
            </Tabs>         
          <Card style={{ width: '500px' }}>
            <Card.Body>

                {/* Agrega más campos de formulario según tus necesidades */}

                <Button variant="primary" type="submit">
                  Enviar
                </Button>
            </Card.Body>
          </Card>
          </Form>
        </div>
      </div>
    </main>
  );
};

export default InformacionFacturacion;
