import React, { useState } from 'react';
import Columns from './Columns';
import Talleres from './Talleres';
import Matrix from './Matrix';
import Navbar from './Navbar';

const Main = ({ darkMode, selectedNavItem, selectedSubNavItem, onItemClick }) => {
  const [currentSection, setCurrentSection] = useState(null); 

  const renderContent = () => {
    switch (selectedNavItem) {
      case 'Home':
        return <Matrix darkMode={darkMode} selectedNavItem={handleItemClick} />;
      case 'Talleres':
        return <Talleres />;
      case 'Marcas':
        return <Columns />;
      case 'Servicios':
        return <Columns />;
      case 'OtraSeccion':
        return <Talleres />;
      default:
        return <Matrix />;
    }
  };

  const mainClasses = darkMode ? 'bg-dark text-light container-fluid' : 'bg-light text-dark container-fluid';

  return (
    <main className={`main-section ${mainClasses}`}>
      <div className="row vh-100">
        <div className="col-12">
          {renderContent()}
        </div>
      </div>
    </main>
  );
};

export default Main;
