import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Matrix = () => {
  const [matrixData, setMatrixData] = useState([
    ['', '', ''],
    ['', '', ''],
    ['', '', ''],
  ]);

  const handleDrop = (targetRowIndex, targetColumnIndex, draggedItem) => {
    const updatedMatrix = [...matrixData];
    updatedMatrix[targetRowIndex][targetColumnIndex] = draggedItem;
    setMatrixData(updatedMatrix);
  };

  return (
    <div className="matrix">
      {matrixData.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((item, columnIndex) => (
            <div
              key={columnIndex}
              className="item"
              onDrop={(e) => {
                e.preventDefault();
                const draggedItem = e.dataTransfer.getData('text');
                handleDrop(rowIndex, columnIndex, draggedItem);
              }}
              onDragOver={(e) => e.preventDefault()}
            >
              {item && <img src={item} alt="Dragged item" />}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Matrix;
