import React, { useState } from 'react';
import { Card, Form, Button } from 'react-bootstrap';
import { Tab, Tabs } from 'react-bootstrap';
import '@fortawesome/fontawesome-free/css/all.min.css';
import { faCheckCircle } from '@fontawesome/free-solid-svg-icons';

const InformacionFacturacion = ({ darkMode, changeComponent, selectedImages }) => {
  const mainClasses = darkMode ? 'bg-dark text-light container-fluid' : 'bg-light text-dark container-fluid';
  const [activeTab, setActiveTab] = useState('tab1');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');
  const [InfoFacturacion, setInfoFacturacion] = useState(false);
  const [isFieldValidated, setFieldValidated] = useState({});
  const [errorFields, setErrorFields] = useState([]);
  const [fieldValid, setFieldValid] = useState({
    txtEmail1: false,
    txtTelefono1: false,
    txtDireccion1: false,
    // Agrega más campos aquí si es necesario
  });

  const handleInfoFacturacion = () => {
    setInfoFacturacion(!InfoFacturacion);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica de envío del formulario
  };

  const validateEmail = (value) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(value);
  };

  const handleChangeEmail = (event) => {
    const value = event.target.value;
    setEmail(value);
  };

  const handleChangeTelefono = (event) => {
    setTelefono(event.target.value);
  };

  const validateField = (value, required, fieldType, fieldName) => {
    let isValid = true;

    // Realiza la validación correspondiente al tipo de campo

    // Si el campo es válido, actualiza el estado fieldValid
    if (isValid) {
      setFieldValid((prevState) => ({
        ...prevState,
        [fieldName]: true,
      }));
    }

    // Retorna el resultado de la validación
    return isValid;
  };

  const handleBlur = (event) => {
    const fieldId = event.target.id;
    const fieldValue = event.target.value;
    const isRequired = event.target.required;
    const fieldType = event.target.getAttribute('data-field-type');
    const isValid = validateField(fieldValue, isRequired, fieldType, fieldId);

    // Actualiza el estado de validación para el campo correspondiente
    // utilizando el ID del campo como clave del estado
    setFieldValidated((prevValidation) => ({
      ...prevValidation,
      [fieldId]: isValid,
    }));

    // Actualiza el estado de errorFields si el campo es obligatorio y no es válido
    setErrorFields((prevErrorFields) => {
      if (isRequired && !isValid) {
        return [...prevErrorFields, fieldId];
      } else {
        return prevErrorFields.filter((errorField) => errorField !== fieldId);
      }
    });
  };

  return (
    <main className={`main-section ${mainClasses}`}>
      <div className="row vh-100 align-items-start pt-4">
        <div className="col-12 d-flex justify-content-center">
          <Form onSubmit={handleSubmit}>
            <Tabs activeKey={activeTab} onSelect={(key) => setActiveTab(key)}>
              <Tab eventKey="tab1" title="Informacion de Facturacion">
                <Card className="mt-4">
                  <Card.Body>
                    <Form.Group controlId="frmEmail1">
                      <Form.Label>Correo Electrónico</Form.Label>
                      <div className="input-container">
                        <Form.Control
                          maxLength={25}
                          id="txtEmail1"
                          type="email"
                          placeholder="Ingrese su correo electrónico"
                          value={email}
                          onChange={handleChangeEmail}
                          required
                          onBlur={handleBlur}
                          className={`form-control ${isFieldValidated['txtEmail1'] === false ? 'is-invalid' : ''}`}
                          data-field-type="email"
                        />
                        {fieldValid.txtEmail1 && (
                          <FontAwesomeIcon icon={faCheckCircle} className="valid-icon" />
                        )}
                      </div>
                      {isFieldValidated['txtEmail1'] === false && (
                        <div className="invalid-feedback">Ingrese un correo electrónico válido.</div>
                      )}
                    </Form.Group>
                    <Form.Group controlId="frmTelefono1">
                      <Form.Label>Teléfono</Form.Label>
                      <div className="input-container">
                        <Form.Control
                          maxLength={10}
                          id="txtTelefono1"
                          type="text"
                          placeholder="Ingrese su número de teléfono"
                          value={telefono}
                          onChange={handleChangeTelefono}
                          onBlur={handleBlur}
                          className={`form-control ${isFieldValidated['txtTelefono1'] === false ? 'is-invalid' : ''}`}
                          data-field-type="phone"
                        />
                        {fieldValid.txtTelefono1 && (
                          <FontAwesomeIcon icon={faCheckCircle} className="valid-icon" />
                        )}
                      </div>
                      {isFieldValidated['txtTelefono1'] === false && (
                        <div className="invalid-feedback">Ingrese un número de teléfono válido.</div>
                      )}
                    </Form.Group>
                  </Card.Body>
                </Card>
              </Tab>
            </Tabs>
            <Button className="mt-4 float-end" type="submit">
              Guardar
            </Button>
          </Form>
        </div>
      </div>
    </main>
  );
};

export default InformacionFacturacion;
