import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import 'bootstrap/dist/js/bootstrap.min.js';

const DarkMode = () => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <nav className={`navbar navbar-expand-lg ${darkMode ? 'bg-dark' : 'navbar-light bg-light'}`}>
      <div className="container-fluid">
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            {/* Aquí puedes agregar los elementos de la lista de navegación si es necesario */}
          </ul>
          <ul className="nav navbar-nav navbar-right">
            <div className="ms-auto form-check form-switch">
              <input className="form-check-input" type="checkbox" id="darkModeSwitch" checked={darkMode} onChange={toggleDarkMode} />
              <label className={`form-check-label ${darkMode ? 'text-light' : ''}`} htmlFor="darkModeSwitch">
                {darkMode ? 'Modo Claro' : 'Modo Oscuro'}
              </label>
            </div>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default DarkMode;
