import React, { useState } from 'react';
import { Card, Form, Button } from 'react-bootstrap';
import {  } from 'react-bootstrap';

const InformacionFacturacion = ({ darkMode, changeComponent, selectedImages }) => {
  const mainClasses = darkMode ? 'bg-dark text-light container-fluid' : 'bg-light text-dark container-fluid';
  const [activeTab, setActiveTab] = useState('tab1');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');
  const [InfoFacturacion, setInfoFacturacion] = useState(false);
  const [isFieldValidated, setFieldValidated] = useState({});
  const [errorFields, setErrorFields] = useState([]);

  const handleInfoFacturacion = () => {
    setInfoFacturacion(!InfoFacturacion);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica de envío del formulario
  };

  const validateEmail = (value) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(value);
  };

  const validateNumber = (value) => {
    const numberRegex = /^\d+$/;
    return numberRegex.test(value);
  };

  const validateText = (value) => {
    return value.trim() !== '';
  };

  const validateField = (value, required, type) => {
    if (required && value.trim() === '') {
      return false; // Campo obligatorio y vacío, no es válido
    }
    if (required && type === 'email' && !validateEmail(value)) {
      return false; // Campo obligatorio y no cumple con la validación de correo electrónico, no es válido
    }
    if (required && type === 'number' && !validateNumber(value)) {
      return false; // Campo obligatorio y no cumple con la validación de número, no es válido
    }
    if (required && type === 'text' && !validateText(value)) {
      return false; // Campo obligatorio y está vacío o contiene solo espacios en blanco, no es válido
    }
    return true; // Campo válido
  };

  const handleChangeEmail = (event) => {
    const value = event.target.value;
    setEmail(value);
  };

  const handleChangeTelefono = (event) => {
    setTelefono(event.target.value);
  };

  const handleBlur = (event) => {
    const fieldId = event.target.id;
    const fieldValue = event.target.value;
    const isRequired = event.target.required;
    const fieldType = event.target.getAttribute('data-field-type');
    const isValid = validateField(fieldValue, isRequired, fieldType);

    // Actualiza el estado de validación para el campo correspondiente
    // utilizando el ID del campo como clave del estado
    setFieldValidated((prevValidation) => ({
      ...prevValidation,
      [fieldId]: isValid,
    }));

    // Actualiza el estado de errorFields si el campo es obligatorio y no es válido
    setErrorFields((prevErrorFields) => {
      if (isRequired && !isValid) {
        return [...prevErrorFields, fieldId];
      } else {
        return prevErrorFields.filter((errorField) => errorField !== fieldId);
      }
    });
  };

  return (
    <main className={`main-section ${mainClasses}`}>
      <div className="row vh-100 align-items-start pt-4">
        <div className="col-12 d-flex justify-content-center">
          <Card className="w-50 p-4">
            <h2 className="mb-4">Información de Facturación</h2>
            <Tabs activeKey={activeTab} onSelect={(key) => setActiveTab(key)}>
              <Tab eventKey="tab1" title="Datos Personales">
                <Form onSubmit={handleSubmit}>
                  <Form.Group controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                      type="email"
                      placeholder="Ingresa tu email"
                      value={email}
                      onChange={handleChangeEmail}
                      onBlur={handleBlur}
                      required
                      data-field-type="email"
                      isInvalid={isFieldValidated['email'] === false}
                    />
                    {isFieldValidated['email'] === false && (
                      <Form.Control.Feedback type="invalid">Ingresa un correo electrónico válido</Form.Control.Feedback>
                    )}
                  </Form.Group>
                  <Form.Group controlId="telefono">
                    <Form.Label>Teléfono</Form.Label>
                    <Form.Control
                      type="text"
                      placeholder="Ingresa tu número de teléfono"
                      value={telefono}
                      onChange={handleChangeTelefono}
                      onBlur={handleBlur}
                      data-field-type="number"
                      isInvalid={isFieldValidated['telefono'] === false}
                    />
                    {isFieldValidated['telefono'] === false && (
                      <Form.Control.Feedback type="invalid">Ingresa un número de teléfono válido</Form.Control.Feedback>
                    )}
                  </Form.Group>
                  <Button variant="primary" type="submit">
                    Enviar
                  </Button>
                </Form>
              </Tab>
              <Tab eventKey="tab2" title="Información de Facturación">
                <Form.Check
                  type="switch"
                  id="infoFacturacion"
                  label="¿Deseas recibir información de facturación?"
                  checked={InfoFacturacion}
                  onChange={handleInfoFacturacion}
                />
                {InfoFacturacion && (
                  <Form onSubmit={handleSubmit}>
                    <Form.Group controlId="razonSocial">
                      <Form.Label>Razón Social</Form.Label>
                      <Form.Control
                        type="text"
                        placeholder="Ingresa tu razón social"
                        onBlur={handleBlur}
                        data-field-type="text"
                        isInvalid={isFieldValidated['razonSocial'] === false}
                      />
                      {isFieldValidated['razonSocial'] === false && (
                        <Form.Control.Feedback type="invalid">Ingresa una razón social válida</Form.Control.Feedback>
                      )}
                    </Form.Group>
                    <Form.Group controlId="direccion">
                      <Form.Label>Dirección</Form.Label>
                      <Form.Control
                        type="text"
                        placeholder="Ingresa tu dirección"
                        onBlur={handleBlur}
                        data-field-type="text"
                        isInvalid={isFieldValidated['direccion'] === false}
                      />
                      {isFieldValidated['direccion'] === false && (
                        <Form.Control.Feedback type="invalid">Ingresa una dirección válida</Form.Control.Feedback>
                      )}
                    </Form.Group>
                    <Button variant="primary" type="submit">
                      Enviar
                    </Button>
                  </Form>
                )}
              </Tab>
            </Tabs>
          </Card>
        </div>
      </div>
    </main>
  );
};

export default InformacionFacturacion;
