import React, { useState } from 'react';
import { Card, Form, Button, Tab, Tabs } from 'react-bootstrap';

const InformacionFacturacion = ({ darkMode, changeComponent, selectedImages }) => {
  const mainClasses = darkMode ? 'bg-dark text-light container-fluid' : 'bg-light text-dark container-fluid';
  const [activeTab, setActiveTab] = useState('tab1');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');
  const [InfoFacturacion, setInfoFacturacion] = useState(false);
  const [isFieldValidated, setFieldValidated] = useState({});
  const [errorFields, setErrorFields] = useState([]);
  const [formValues, setFormValues] = useState({
    tipoIdentificacion: '',
    numeroIdentificacion: '',
    nombres: '',
    apellidos: '',
    email: '',
    telefono: '',
    direccion: ''
  });

  const handleInfoFacturacion = () => {
    setInfoFacturacion(prevState => !prevState);
    if (InfoFacturacion) {
      const {
        tipoIdentificacion,
        numeroIdentificacion,
        nombres,
        apellidos,
        email,
        telefono,
        direccion
      } = formValues;

      setFormValues(prevFormValues => ({
        ...prevFormValues,
        tipoIdentificacion1: tipoIdentificacion,
        numeroIdentificacion1: numeroIdentificacion,
        nombres1: nombres,
        apellidos1: apellidos,
        email1: email,
        telefono1: telefono,
        direccion1: direccion
      }));
    } else {
      setFormValues(prevFormValues => ({
        ...prevFormValues,
        tipoIdentificacion1: '',
        numeroIdentificacion1: '',
        nombres1: '',
        apellidos1: '',
        email1: '',
        telefono1: '',
        direccion1: ''
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica de envío del formulario
  };

  const validateEmail = (value) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(value);
  };

  const validateNumber = (value) => {
    const numberRegex = /^\d+$/;
    return numberRegex.test(value);
  };

  const validateText = (value) => {
    return value.trim() !== '';
  };

  const validateField = (value, required, type) => {
    if (required && value.trim() === '') {
      return false; // Campo obligatorio y vacío, no es válido
    }
    if (required && type === 'email' && !validateEmail(value)) {
      return false; // Campo obligatorio y no cumple con la validación de correo electrónico, no es válido
    }
    if (required && type === 'number' && !validateNumber(value)) {
      return false; // Campo obligatorio y no cumple con la validación de número, no es válido
    }
    if (required && type === 'text' && !validateText(value)) {
      return false; // Campo obligatorio y está vacío o contiene solo espacios en blanco, no es válido
    }
    return true; // Campo válido
  };

  const handleChangeEmail = (event) => {
    const value = event.target.value;
    setEmail(value);
  };

  const handleChangeTelefono = (event) => {
    setTelefono(event.target.value);
  };

  const handleBlur = (event) => {
    const fieldId = event.target.id;
    const value = event.target.value;
    const { required, type } = event.target.dataset;
    const isValid = validateField(value, required === 'true', type);
    setFieldValidated(prevState => ({
      ...prevState,
      [fieldId]: true
    }));
    if (!isValid && !errorFields.includes(fieldId)) {
      setErrorFields(prevState => [...prevState, fieldId]);
    }
    if (isValid && errorFields.includes(fieldId)) {
      setErrorFields(prevState => prevState.filter(id => id !== fieldId));
    }
  };

  const renderTabContent = () => {
    if (activeTab === 'tab1') {
      return (
        <div>
          <h4>Tab 1 Content</h4>
          <p>This is the content of Tab 1.</p>
        </div>
      );
    } else if (activeTab === 'tab2') {
      return (
        <div>
          <h4>Tab 2 Content</h4>
          <p>This is the content of Tab 2.</p>
        </div>
      );
    }
    return null;
  };

  return (
    <Card className={mainClasses}>
      <Card.Header>
        <h5>Información de facturación</h5>
      </Card.Header>
      <Card.Body>
        <Tabs activeKey={activeTab} onSelect={(key) => setActiveTab(key)}>
          <Tab eventKey="tab1" title="Tab 1">
            {renderTabContent()}
          </Tab>
          <Tab eventKey="tab2" title="Tab 2">
            {renderTabContent()}
          </Tab>
        </Tabs>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formEmail">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              value={email}
              onChange={handleChangeEmail}
              onBlur={handleBlur}
              data-required="true"
              data-type="email"
              isValid={isFieldValidated['formEmail'] && !errorFields.includes('formEmail')}
              isInvalid={isFieldValidated['formEmail'] && errorFields.includes('formEmail')}
            />
          </Form.Group>
          <Form.Group controlId="formTelefono">
            <Form.Label>Teléfono</Form.Label>
            <Form.Control
              type="text"
              value={telefono}
              onChange={handleChangeTelefono}
              onBlur={handleBlur}
              data-required="true"
              data-type="number"
              isValid={isFieldValidated['formTelefono'] && !errorFields.includes('formTelefono')}
              isInvalid={isFieldValidated['formTelefono'] && errorFields.includes('formTelefono')}
            />
            <Form.Control.Feedback type="invalid">Please enter a valid phone number.</Form.Control.Feedback>
          </Form.Group>
          <Form.Group controlId="formInfoFacturacion">
            <Form.Check
              type="checkbox"
              label="¿La información de facturación es la misma que la de entrega?"
              checked={InfoFacturacion}
              onChange={handleInfoFacturacion}
            />
          </Form.Group>
          {!InfoFacturacion && (
            <div>
              <Form.Group controlId="cboTipoIdentificacion">
                <Form.Label>Tipo de identificación</Form.Label>
                <Form.Control as="select" data-required="true">
                  <option value="">Seleccione...</option>
                  <option value="CC">Cédula de ciudadanía</option>
                  <option value="TI">Tarjeta de identidad</option>
                  <option value="CE">Cédula de extranjería</option>
                  <option value="NIT">NIT</option>
                </Form.Control>
              </Form.Group>
              <Form.Group controlId="txtNumeroIdentificacion">
                <Form.Label>Número de identificación</Form.Label>
                <Form.Control
                  type="text"
                  data-required="true"
                  data-type="number"
                  isValid={isFieldValidated['txtNumeroIdentificacion'] && !errorFields.includes('txtNumeroIdentificacion')}
                  isInvalid={isFieldValidated['txtNumeroIdentificacion'] && errorFields.includes('txtNumeroIdentificacion')}
                />
                <Form.Control.Feedback type="invalid">Please enter a valid identification number.</Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="txtNombres">
                <Form.Label>Nombres</Form.Label>
                <Form.Control
                  type="text"
                  data-required="true"
                  isValid={isFieldValidated['txtNombres'] && !errorFields.includes('txtNombres')}
                  isInvalid={isFieldValidated['txtNombres'] && errorFields.includes('txtNombres')}
                />
                <Form.Control.Feedback type="invalid">Please enter your first name.</Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="txtApellidos">
                <Form.Label>Apellidos</Form.Label>
                <Form.Control
                  type="text"
                  data-required="true"
                  isValid={isFieldValidated['txtApellidos'] && !errorFields.includes('txtApellidos')}
                  isInvalid={isFieldValidated['txtApellidos'] && errorFields.includes('txtApellidos')}
                />
                <Form.Control.Feedback type="invalid">Please enter your last name.</Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="txtEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  data-required="true"
                  isValid={isFieldValidated['txtEmail'] && !errorFields.includes('txtEmail')}
                  isInvalid={isFieldValidated['txtEmail'] && errorFields.includes('txtEmail')}
                />
                <Form.Control.Feedback type="invalid">Please enter a valid email.</Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="txtTelefono">
                <Form.Label>Teléfono</Form.Label>
                <Form.Control
                  type="text"
                  data-required="true"
                  data-type="number"
                  isValid={isFieldValidated['txtTelefono'] && !errorFields.includes('txtTelefono')}
                  isInvalid={isFieldValidated['txtTelefono'] && errorFields.includes('txtTelefono')}
                />
                <Form.Control.Feedback type="invalid">Please enter a valid phone number.</Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="txtDireccion">
                <Form.Label>Dirección</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={3}
                  data-required="true"
                  isValid={isFieldValidated['txtDireccion'] && !errorFields.includes('txtDireccion')}
                  isInvalid={isFieldValidated['txtDireccion'] && errorFields.includes('txtDireccion')}
                />
                <Form.Control.Feedback type="invalid">Please enter your address.</Form.Control.Feedback>
              </Form.Group>
            </div>
          )}
          <Button type="submit">Submit</Button>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default InformacionFacturacion;
