import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Matrix = ({ darkMode }) => {
  const matrixClasses = darkMode ? 'bg-dark text-light' : 'bg-light';
  const itemClasses = darkMode ? 'border-light' : 'border-dark';

  return (
    <div className={`matrix ${matrixClasses}`}>
      {matrixData.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((item, columnIndex) => (
            <div
              key={columnIndex}
              className={`item border ${itemClasses}`}
              draggable={true}
              onDragStart={() => setDraggedItem({ row: rowIndex, column: columnIndex })}
              onDragEnd={() => setDraggedItem(null)}
            >
              {item}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Matrix;
